
#define INDEX_Page "<!DOCTYPE html><html><head>    <script type=\"text/javascript\" >window.onload = init;function init() {let btns = document.getElementsByTagName(\"input\");for (let i = 0; i < btns.length; i++) {btns[i].onclick = BtnClick;}}function BtnClick(event){let req = new XMLHttpRequest();req.open(\"GET\", \"./led/\"+event.currentTarget.id, true);req.send();}</script></head><body><input type=\"button\" value=\"Wei&szlig;\" id=\"white\" /><input type=\"button\" value=\"Rot\" id=\"red\" /><input type=\"button\" value=\"Gr&uuml;n\" id=\"green\" /><input type=\"button\" value=\"Blau\" id=\"blue\" /><input type=\"button\" value=\"Schwarz\" id=\"black\" /><input type=\"button\" value=\"Regenbogen\" id=\"rainbow\" /></body></html>"
